%Function Prototype for Piecewise Definition

function x = xt(t);
    if (t < - 1)
        x = -1;
    elseif (t >= -1 && t < 0 )
        x = t;
    elseif (t >= 0 && t < 2 )
        x = 2;
    elseif (t >= 2 && t < 4)
        x = 4-t;
    else
        x = 0;
    end
end
%%
%For Signal A (original signal)

t = -5 : 0.001 : 5; % you may have to adjust the start and end value of t
x = zeros(size(t));
for i = 1 : 1 : length(x)
 x(i) = xt( t(i) );
end
disp("Plot for Original Signal") %[output:4f441272]
plot(t,x); %[output:8c122d09]
axis equal;  %[output:8c122d09]
%%
%For signal B

t = -5 : 0.001 : 5; % you may have to adjust the start and end value of t
x = zeros(size(t));
for i = 1 : 1 : length(x)
 x(i) = xt( - (t(i) - 3) );
end
disp("Plot for Signal with Function: x(-(t-3)) ") %[output:2f5d91c9]
plot(t,x); %[output:8df507c2]
axis equal;  %[output:8df507c2]
%%
%For Signal C

t = -5 : 0.001 : 5; % you may have to adjust the start and end value of t
x = zeros(size(t));
for i = 1 : 1 : length(x)
 x(i) = xt( 2 * (t(i) - 2) );
end
disp("Plot for Signal with Function: x(2(t-2)) ") %[output:8ba04635]
plot(t,x); %[output:68ae40e0]
axis equal;  %[output:68ae40e0]
